exec sp_executesql N'SELECT A.Subunitate,A.Cont,A.Ziua,A.Data,A.Jurnal,A.Numar,A.Valuta,A.Curs,A.Total_plati,A.Total_incasari,A.Stare,A.Numar_pozitii,B.Subunitate,B.Cont,B.Denumire_cont,B.Are_analitice,B.Sold_debit,B.Sold_credit,B.Tip_cont FROM  TET ..plin A LEFT OUTER JOIN TET ..conturi B ON B.Subunitate = A.Subunitate AND B.Cont = A.Cont WHERE A.Subunitate = @P1 AND A.Cont >= @P2 AND (A.Data >= @P3 AND A.Data < DATEADD (day, 1, @P4))    AND ((0x00=0 or charindex('';''+rtrim(A.Cont)+'';'','';''+RTrim('';'')+'';'')>0)) ORDER BY A.Subunitate ASC ,A.Data ASC ,A.Cont ASC ,A.Jurnal ASC ',N'@P1 char(9),@P2 char(13),@P3 datetime,@P4 datetime','1        ','             ','2013-01-01 00:00:00','2013-01-31 00:00:00'