insert pozdoc (Subunitate,Tip,Numar,Cod,Data,Gestiune,Cantitate,Pret_valuta,Pret_de_stoc,Adaos,Pret_vanzare,Pret_cu_amanuntul,TVA_deductibil,Cota_TVA,Utilizator,Data_operarii,Ora_operarii,Cod_intrare,Cont_de_stoc,Cont_corespondent,TVA_neexigibil,Pret_amanunt_predator,Tip_miscare,Locatie,Data_expirarii,Numar_pozitie,Loc_de_munca,Comanda,Barcod,Cont_intermediar,Cont_venituri,Discount,Tert,Factura,Gestiune_primitoare,Numar_DVI,Stare,Grupa,Cont_factura,Valuta,Curs,Data_facturii,Data_scadentei,Procent_vama,Suprataxe_vama,Accize_cumparare,Accize_datorate,Contract,Jurnal)
select s.Subunitate,s.Tip,s.Numar,s.Cod,s.Data,s.Gestiune,s.Cantitate,s.Pret_valuta,s.Pret_de_stoc,s.Adaos,s.Pret_vanzare,s.Pret_cu_amanuntul,s.TVA_deductibil,s.Cota_TVA,s.Utilizator,s.Data_operarii,s.Ora_operarii,s.Cod_intrare,s.Cont_de_stoc,s.Cont_corespondent,s.TVA_neexigibil,s.Pret_amanunt_predator,s.Tip_miscare,s.Locatie,s.Data_expirarii,s.Numar_pozitie,s.Loc_de_munca,s.Comanda,s.Barcod,s.Cont_intermediar,s.Cont_venituri,s.Discount,s.Tert,s.Factura,s.Gestiune_primitoare,s.Numar_DVI,s.Stare,s.Grupa,s.Cont_factura,s.Valuta,s.Curs,s.Data_facturii,s.Data_scadentei,s.Procent_vama,s.Suprataxe_vama,s.Accize_cumparare,s.Accize_datorate,s.Contract,s.Jurnal
from sysspd s inner join pozdoc p on p.Cod=s.Cod and p.Cod_intrare=s.Cod_intrare and p.Gestiune=s.Gestiune 
where s.Factura='12171'
and p.Contract='12171' and s.Stergator='vatajelu'
